.top-nav-pannel {
  position: fixed;
  bottom: 70px;
  height: 135px;
  background: none;
  width: 100%;
  color: #fff;
  font-size: 14px;
  z-index: 10004;
  button {
    background: none;
    border: 1px #8c8c8c solid;
    padding: 5px 40px;
    color: #fff;
    display: inline-block;
    border-radius: 16px;
    &:focus {
      outline:none;
    }
  }
  .child-mod {
    padding: 5px 20px;
    margin-top: 15px;
    & > span:first-child {
      margin-right: 20px;
    }
  }
  .bk-container {
    position: relative;
    height: 30px;
    width: 30px;
    background: #fff;
    border-radius: 15px;
    display: inline-block;
    vertical-align: -14px;
    margin-left: 10px;
    .color_btn {
      height: 30px;
      width: 30px;
      border-radius: 15px;
    }
  }
  .bk-container-current {
    height: 31px;
    width: 32px;
    border-radius: 16px;
    border: 1px #ff7800 solid;
  }
  .bac(@color) {
    background-color: @color;
  }
  .bk-container:nth-child(2) .color_btn {
    .bac(#e9dfc7);
  }
  .bk-container:nth-child(3) .color_btn {
    .bac(#e7eee5);
  }
  .bk-container:nth-child(4) .color_btn {
    .bac(#a4a4a4);
  }
  .bk-container:nth-child(5) .color_btn {
    .bac(#cdefcd);
  }
  .bk-container:nth-child(6) .color_btn {
    .bac(#283548);
  }
  .bk-container:nth-child(7) .color_btn {
    .bac(#0f1410);
  }
}
